<?php$id=(isset($_GET['id']) ? $_GET['id'] : "");$action=NULL;$categories=get_categories();$titre_rubrique_parente="";$id_rubrique_parente = "";$id_type_lien = "";$titre = "";$url = "";$type_lien = "";$texte = "";$active = NULL;if (isset($_POST['poste'])){	switch ($_POST['action']){		case "ajouter":				$id_article=ajouter_article($_POST["rubrique_parente"], $_POST["titre"], $_POST["url"], $_POST["type_lien"], $_POST["texte"], $active=(isset($_POST['active'])? 1 : 0));								if ($id_article){					$action="consulter";					$id=$id_article;					$message="L'article a été correctement ajouté.";				}				else{					$action="ajouter";					$message="L'insertion a échoué.";				}			break;					case "modifier":				$modifie=modifier_article($_POST['id'], $_POST["rubrique_parente"], $_POST["titre"], $_POST["url"], $_POST["type_lien"], $_POST["texte"], $active=(isset($_POST['active'])? 1 : 0));								if ($modifie){					$action="consulter";					$id=$_POST["id"];					$message="L'article a été correctement éditée.";				}				else{					$action="modifier";					$message="La mise à jour a échoué";				}						break;					default : 				$message="Une erreur est survenue";			break;		}}if(isset($_GET['supprimer'])){	$supprime=supprimer_un_article($id);	if ($supprime){										$message="L'article a été correctement supprimé.";				}				else{					$action="consulter";					$message="La suppression a échoué.";				}}if(isset($_GET['ajouter']) AND $action!="consulter"){	$action="ajouter";}if((isset($_GET['consulter']) && isset($_GET['id'])) OR $action=="consulter"){	$action="consulter";	$le_article=get_un_article($id);	$rubrique_parente = get_une_rubrique($le_article['id_rubrique']);	$titre_rubrique_parente = $rubrique_parente['rub_titre'];	$titre_categorie_parente = get_titre_categorie($rubrique_parente['id_cat']);	$id_type_lien = $le_article['art_type_lien'];	$libelle_type_lien = get_libelle_type_lien($id_type_lien);}if((isset($_GET['modifier']) AND isset($_GET['id']) AND $action!="consulter") OR ($action=="modifier")){	$action="modifier";	$le_article=get_un_article($id);	$rubrique_parente = get_une_rubrique($le_article['id_rubrique']);	$titre_rubrique_parente = $rubrique_parente['rub_titre'];		$id_rubrique_parente = $rubrique_parente['id_rubrique'];	$titre = $le_article['art_titre'];	$url = $le_article['art_url'];	$id_type_lien = $le_article['art_type_lien'];	$libelle_type_lien = get_libelle_type_lien($id_type_lien);	$texte = $le_article['art_texte'];	$active = $le_article['active'];	}$types_liens=get_types_liens();$categories=get_categories();$rubriques=get_toutes_les_rubriques();$articles=get_tous_les_articles();include ("v/v_articles.php");