<?php$id=(isset($_GET['id']) ? $_GET['id'] : "");$action=NULL;$categories=get_categories();$titre_categorie_parente="";$id_categorie_parente = $id;$titre = "";$texte = "";$vignette = "";$active = NULL;if (isset($_POST['poste'])){	switch ($_POST['action']){		case "ajouter":				//echo "<pre>";print_r($_POST);exit();				$id_rubrique=ajouter_rubrique($_POST["categorie_parente"], $_POST["titre"], $_POST["texte"], $_POST["vignette"], $active=(isset($_POST['active'])? 1 : 0));								if ($id_rubrique){					$action="consulter";					$id=$id_rubrique;					$message="La rubrique a été correctement ajoutée.";				}				else{					$action="ajouter";					$message="L'insertion a échoué.";				}			break;					case "modifier":				$modifie=modifier_rubrique($_POST["id"],  $_POST["categorie_parente"], $_POST["titre"], $_POST["texte"], $_POST["vignette"], $active=(isset($_POST['active'])? 1 : 0));								if ($modifie){					$action="consulter";					$id=$_POST["id"];					$message="La rubrique a été correctement éditée.";				}				else{					$action="modifier";					$message="La mise à jour a échoué";				}						break;					default : 				$message="Une erreur est survenue";			break;		}}if(isset($_GET['supprimer'])){	$supprime=supprimer_une_rubrique($id);	if ($supprime){										$message="La rubrique a été correctement supprimée.";				}				else{					$action="consulter";					$message="La suppression a échoué.";				}}if(isset($_GET['ajouter']) AND $action!="consulter"){	$action="ajouter";}if((isset($_GET['consulter']) && isset($_GET['id'])) OR $action=="consulter"){	$action="consulter";	$la_rubrique=get_une_rubrique($id);	$rub_articles=get_articles($la_rubrique['id_rubrique']);	$titre_categorie_parente = get_titre_categorie($la_rubrique['id_cat']);}if((isset($_GET['modifier']) AND isset($_GET['id']) AND $action!="consulter") OR ($action=="modifier")){	$action="modifier";	$la_rubrique=get_une_rubrique($id);	$titre_categorie_parente = get_titre_categorie($la_rubrique['id_cat']);	$id_categorie_parente = $la_rubrique['id_cat'];	$titre = $la_rubrique['rub_titre'];	$texte = $la_rubrique['rub_texte'];	$vignette = $la_rubrique['rub_image'];	$active = $la_rubrique['active'];}$categories=get_categories();$rubriques=get_toutes_les_rubriques();$articles=get_tous_les_articles();include ("v/v_rubriques.php");